import React from 'react';

let currDate=new Date();
currDate=currDate.getHours();
let greeting=" "
const cssStyle={}
function Heading(){
    if (currDate>=1 && currDate<12){
        greeting="Good Morning";
        cssStyle.color="Green";
        return(
            <img src="https://photomedia.in/wp-content/uploads/2019/09/good-morning-images-7.jpg" alt='Greet' className='image'/>
          )
    }else if (currDate>=12 && currDate<19){
        greeting="Good Afternoon";
        cssStyle.color="Orange";
        return(
            <img src="https://www.goodmorningimagesdownload.com/wp-content/uploads/2020/03/Good-Afternoon-Images-19.jpg" alt='Greet' className='image'/>
          )
    }else{
        greeting="Good Night";
        cssStyle.color="Blue";
        return(
            <img src="https://th.bing.com/th/id/OIP.PIpeBp6LvxV864VG2-TqPQHaFh?pid=ImgDet&rs=1" alt='Greet' className='image'/>
          )
    }
    return(
        <h1>Hello everyone, <span style={cssStyle}> {greeting}</span></h1>
    )
}
export default Heading;
